import React, { useState } from "react";
import { minutesToDuration } from "./utils/duration";
import { secondsToDuration } from "./utils/duration";

function PomodoroTimerView ({session, durations}) {

    const sessionTotalDurationView = (label) => {
        return minutesToDuration(
            label === "Break" ?  durations.focusDuration : durations.breakDuration
            );
    }

    if(session.label !== ""){
        return (
            <>
                <div className="row mb-2">
                <div className="col">
                    {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
                    <h2 data-testid="session-title">
                                            {/*make this into its own function and call it instead of having the conditional in the JSX */}
                    {session.label} for {sessionTotalDurationView(session.label)} minutes
                    </h2>
                    <p className="lead" data-testid="session-sub-title">
                    {secondsToDuration(session?.timeRemaining)} remaining
                    </p>
                </div>
                </div>
                <div className="row mb-2">
                <div className="col">
                    <div className="progress" style={{ height: "20px" }}>
                    <div

                        className="progress-bar"
                        role="progressbar"
                        aria-valuemin="0"
                        aria-valuemax="100"
                        aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                        style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
                    />
                    </div>
                </div>
                </div>
            </>
        )
    }
    return null;
};

export default PomodoroTimerView;
