import React from "react"; 
import classNames from "../utils/class-names";

function PomodoroStartStop ( { isTimerRunning, setIsTimerRunning, durations, setSession, session, initialSessionState, setAriaValues, initialAriaValues } ) {

  
     /**
   * Called whenever the play/pause button is clicked.
   */
    function playPause() {
        /*setIsTimerRunning((prevState) => {
          console.log(prevState)
          const nextState = !prevState;*/
          if (!session.isTimerRunning) {
            console.log('kaw')
            setSession((prevStateSession) => {
              // If the timer is starting and the previous session is null,
              // start a focusing session.
              if (prevStateSession === null) {
                return {
                  isTimerRunning: true,
                  label: "Focusing",
                  timeRemaining: durations.focusDuration * 60,
                  stopDisabled: false
                };
              }
              return prevStateSession;
          })   
      }
    }

    function stop() {
      if(session && session.label !== ""){
        
        setAriaValues(initialAriaValues);

        setSession(initialSessionState);
      }
    }
return(
<div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !session.isTimerRunning,
                  "oi-media-pause": session.isTimerRunning,
                })}
              />
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              data-testid="stop"
              title="Stop the session"
              onClick={stop}
              disabled={session.stopDisabled}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
)
};

export default PomodoroStartStop;
