import React, { useState } from "react";
import { minutesToDuration } from "./utils/duration";
import { secondsToDuration } from "./utils/duration";
import useInterval from "./utils/useInterval";

function nextTick(prevState) {
    const timeRemaining = Math.max(0, prevState.timeRemaining - 1);
    return {
      ...prevState,
      timeRemaining,
    };
  }

function PomodoroTimerView ({ session, durations, setSession, isTimerRunning }) {

    const initialAriaValues = {
        ariaValueNow: 0,
        widthPercent: 0
    }

    const [ariaValues, setAriaValues] = useState({...initialAriaValues})

    function nextSession(focusDuration, breakDuration) {
        /**
         * State function to transition the current session type to the next session. e.g. On Break -> Focusing or Focusing -> On Break
         */
         return (currentSession) => {
           console.log(session.label)
          if (currentSession.label === "Focusing") {
            return {
              label: "On Break",
              timeRemaining: breakDuration * 60,
            };
          }
          return {
            label: "Focusing",
            timeRemaining: focusDuration * 60,
          };
        };
      }
    
      useInterval(() => {
        if (session.timeRemaining === 0) {
          new Audio("https://bigsoundbank.com/UPLOAD/mp3/1482.mp3").play();
          return setSession(nextSession(durations.focusDuration, durations.breakDuration));
        }
        return (
            setSession(nextTick),
            setAriaValues((currentAriaValues) => {
                return (
                    {
                      ariaValueNow: (++ariaValueNow),
                      widthPercent: (++widthPercent)
                    }
                )
            })
        );
      },
      isTimerRunning ? 1000 : null
    );

    const sessionTotalDurationView = (label) => {
        return minutesToDuration(
            label === "Break" ?  durations.focusDuration : durations.breakDuration
            );
    }

    if(session.label !== ""){
        return (
            <>
                <div className="row mb-2">
                <div className="col">
                    {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
                    <h2 data-testid="session-title">
                                            {/*make this into its own function and call it instead of having the conditional in the JSX */}
                    {session.label} for {sessionTotalDurationView(session.label)} minutes
                    </h2>
                    <p className="lead" data-testid="session-sub-title">
                    {secondsToDuration(session?.timeRemaining)} remaining
                    </p>
                </div>
                </div>
                <div className="row mb-2">
                <div className="col">
                    <div className="progress" style={{ height: "20px" }}>
                    <div
                        className="progress-bar"
                        role="progressbar"
                        aria-valuemin="0"
                        aria-valuemax="100"
                        aria-valuenow={ariaValues.ariaValueNow} // TODO: Increase aria-valuenow as elapsed time increases
                        style={{ width: `${ariaValues.widthPercent}%` }} // TODO: Increase width % as elapsed time increases
                    />
                    </div>
                </div>
                </div>
            </>
        )
    }
    return null;
};

export default PomodoroTimerView;
