import React, { useState } from "react";
import { minutesToDuration } from "./utils/duration";


function PomodoroTimeAdjusters ({ durations, setDurations }) {

    const durationDecreaseHandler = (event) => {
        const durationDecreaser = (currentDuration) => {
            currentDuration -= 5;
           const maxDuration = Math.max(5, currentDuration);
           return maxDuration
        }
        setDurations({...durations, focusDuration: durationDecreaser(durations.focusDuration)})
    }

    const durationIncreaseHandler = (event) => {
        const durationIncreaser = (currentDuration) => {
            currentDuration += 5;
           const maxDuration = Math.min(60, currentDuration);
           return maxDuration
        }
        setDurations({...durations, focusDuration: durationIncreaser(durations.focusDuration)})
    }

return (
      // ToDo: Allow the user to adjust the focus and break duration.
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              Focus Duration: {minutesToDuration(durations.focusDuration)}
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                name="decrease-focus"
                onClick={durationDecreaseHandler}
                value={durations.focusDuration}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={durationIncreaseHandler}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                Break Duration: {minutesToDuration(durations.breakDuration)}
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
};

export default PomodoroTimeAdjusters;
