import React, { useState } from "react";
import "./App.css";
import Pomodoro from "./pomodoro/Pomodoro";
import PomodoroTimeAdjusters from "./PomodoroTimeAdjusters";
import PomodoroStartStop from "./PomodoroStartStop";

function App() {
//Timer duration states
  const initialDurationState = {
    focusDuration: 25,
    breakDuration: 5
  };
  const [durations, setDurations] = useState({...initialDurationState})
//Other states
const [isTimerRunning, setIsTimerRunning] = useState(false);
  return (
    <div className="App">
      <header className="App-header container">
        <h1>Pomodoro Timer</h1>
      </header>
      <div className="container">
      <div className="pomodoro">
        <PomodoroTimeAdjusters durations = {durations} setDurations = {setDurations}/>
        <PomodoroStartStop isTimerRunning = {isTimerRunning} setIsTimerRunning = {setIsTimerRunning} durations = {durations}/>
        <Pomodoro durations = {durations}/>

      </div>
      </div>
    </div>
  );
}

export default App;
