import React, { useState } from "react";
import "./App.css";
import Pomodoro from "./pomodoro/Pomodoro";
import PomodoroTimeAdjusters from "./PomodoroTimeAdjusters";
import PomodoroStartStop from "./PomodoroStartStop";
import PomodoroTimerView from "./PomodoroTimerView";

function App() {
//Timer duration states
  const initialDurationState = {
    focusDuration: .5,
    breakDuration: 5
  };
  const [durations, setDurations] = useState({...initialDurationState})


//Other states
// Timer starts out paused
const [isTimerRunning, setIsTimerRunning] = useState(false);

// The current session - null where there is no session running
const initialSessionState = {
  label: "",
  timeRemaining: 0,
  percentComplete: 0
}
const [session, setSession] = useState(null);


  return (
    <div className="App">
      <header className="App-header container">
        <h1>Pomodoro Timer</h1>
      </header>
      <div className="container">
      <div className="pomodoro">
        <PomodoroTimeAdjusters durations = {durations} setDurations = {setDurations} session = {session}/>
        <PomodoroStartStop isTimerRunning = {isTimerRunning} setIsTimerRunning = {setIsTimerRunning} durations = {durations} setSession = {setSession}/>
        <PomodoroTimerView session = {session} durations = {durations}/>
        <Pomodoro durations = {durations} session = {session} setSession = {setSession} isTimerRunning = {isTimerRunning}/>

      </div>
      </div>
    </div>
  );
}

export default App;
