import React, { useState } from "react";
import "./App.css";
import PomodoroTimeAdjusters from "./PomodoroTimeAdjusters";
import PomodoroStartStop from "./pomodoro/PomodoroStartStop";
import PomodoroTimerView from "./PomodoroTimerView";

function App() {
//Timer duration states
  const initialDurationState = {
    focusDuration: 25,
    breakDuration: 5
  };
  const [durations, setDurations] = useState({...initialDurationState})


//Other states
// Timer starts out paused
const [isTimerRunning, setIsTimerRunning] = useState(false);

const initialAriaValues = {
  percentComplete: 0,
  ariaValueNow: 0,

};

const [ariaValues, setAriaValues] = useState(initialAriaValues);

// The current session - null where there is no session running
const initialSessionState = {
  label: "",
  timeRemaining: 0,
  sessionDuration: 0,
  totalSessionTime: 0
}
const [session, setSession] = useState({...initialSessionState});


  return (
    <div className="App">
      <header className="App-header container">
        <h1>Pomodoro Timer</h1>
      </header>
      <div className="container">
      <div className="pomodoro">
          <Pomodoro isTimerRunning = {isTimerRunning} 
        setIsTimerRunning = {setIsTimerRunning} 
        durations = {durations} 
        setSession = {setSession} 
        session={session} 
        initialSessionState={initialSessionState}
        setAriaValues={setAriaValues}
        initialAraiValues={initialAriaValues}
        setDurations={setDurations}
          />
      </div>
      </div>
    </div>
  );
}

export default App;
